import { useContext } from "react";
import { Navigate } from "react-router-dom";
import { AppContext } from "../Context/AppContext";


function PrivateRoute({children}) {

    const {isAuth} = useContext(AppContext)


    if(!isAuth){
        return <Navigate to="/"/>
    }
    return children






}

export default PrivateRoute;
