import { useState } from "react";
import { useEffect } from "react";
import { useParams } from "react-router-dom";


const getSingleRestaurant = ({
  id
}) => {
  return fetch(`https://dbioz2ek0e.execute-api.ap-south-1.amazonaws.com/mockapi/getrestaurants/${id}`).then((res) => res.json())
}

function SingleRestaurantPage() {
  const [singleData,setSingleData] = useState({})


  const {id} = useParams()

  useEffect(() => {
    getSingleRestaurant({id}).then((res) => {

     setSingleData(res.data)
     console.log(res.data);


    })
  },[])


  return (
    <div data-testid="restaurant-container">
      <div>
        <h3 data-testid="restaurant-name"></h3>
      </div>
      <div data-testid="restaurant-type">{singleData.type}</div>
      <div data-testid="restaurant-rating">:{singleData.rating}</div>
      <div data-testid="restaurant-votes">Votes:{singleData.number_of_votes}</div>
      <div data-testid="restaurant-price">Starting Price:{singleData.price_starts_from}</div>
      <div>
        <img data-testid="restaurant-image" src={singleData.image} width={"100px"} />
      </div>
    </div>
  );
}
export default SingleRestaurantPage;
